// Custom Scripts
$(document).ready(function(){
	$("#toggle").click(function(){
		$(".button-container").toggleClass("active");
		$("#menu").toggleClass("open");
	});
	
	/*Smooth Scroll*/
	$(function() {
	  $('.nav-overlay a[href*=#]:not([href=#]), .navbar-right a[href*=#]:not([href=#]), .btn-reg a[href*=#]:not([href=#])').click(function() {
		if (location.pathname.replace(/^\//,'') === this.pathname.replace(/^\//,'') && location.hostname === this.hostname) {
		  var target = $(this.hash);
		  target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
		  if (target.length) {
			$('html,body').animate({
			  scrollTop: target.offset().top-75
			}, 1000);
			return false;
		  }
		}
	  });
	});
	$(function() {
	  $('.nav-overlay a, #btn-signup').click(function() {
		$(".button-container").removeClass("active");
		$("#menu").removeClass("open");
	 });
	});
	$("#share").jsSocials({
		shares: ["email", "twitter", "facebook", "googleplus", "linkedin"]
	});
		
	/*Animation Init*/
	new WOW().init();
});